import React, { useState } from 'react';
import './App.css';

function PressButton({ onPress, children }) {
  // Exposes an `onPress` prop (React Native style) and maps it to a web `onClick`.
  return (
    <button type="button" title="Double" onClick={onPress}>
      {children}
    </button>
  );
}

function PressButton2({ onPress, children }) {
  // Exposes an `onPress` prop (React Native style) and maps it to a web `onClick`.
  return (
    <button type="button" title="Pause" onClick={onPress}>
      {children}
    </button>
  );
}

function App() {
  const [points, setPoints] = useState(1);

  const handlePress = () => setPoints((p) => p * 2);
  if (points >= 2048) {
    alert('You have reached 2048 points!');
    setPoints(1); // Reset points after reaching 2048
  }

  const handlePress2 = () => setPoints((p) => p * 2);
  if (points >= 2048) {
    alert('You have reached 2048 points!');
    setPoints(1); // Reset points after reaching 2048
  }

  return (
    <div className="App">
      <header className="App-header">
        <p>Points: {points}</p>
        <p>
          <PressButton onPress={handlePress}>
            Press this to double your points
          </PressButton>
          <PressButton2 onPress={handlePress2}>
            Press this to make your points 0
          </PressButton2>
        </p>
      </header>
    </div>
  );
}

export default App;
