import React, { useState } from 'react';
import './App.css';

function PressButton({ onPress, children }) {
  // Exposes an `onPress` prop (React Native style) and maps it to a web `onClick`.
  return (
    <button type="button" onClick={onPress}>
      {children}
    </button>
  );
}

function PressButton2({ onPress, children }) {
  // Exposes an `onPress` prop (React Native style) and maps it to a web `onClick`.
  return (
    <button type="button" onClick={onPress}>
      {children}
    </button>
  );
}

function PressButton3({ onPress, children }) {
  // Exposes an `onBuy` prop (React Native style) and maps it to a web `onClick`.
  return (
    <button type="button" onClick={onPress}>
      {children}
    </button>
  );
}

function App() {
  const [points, setPoints] = useState(1);

  const handlePress = () => setPoints((p) => p + 1);

  const handlePress2 = () => setPoints((p) => 1);

  const handleBuy = () => {
    if (points >= 10) {
      setPoints((p) => p - 10);
      alert('Item purchased!');
      setTimeout(handlePress, 3000)
    } else {
      alert('Not enough points to buy this item.');
    }
  return (
    <div className="App">
      <header className="App-header">
        <p>Points: {points}</p>
        <p>
          <PressButton onPress={handlePress}>
            Press this to add your points
          </PressButton>
          <PressButton2 onPress={handlePress2}>
            Press this to make your points 1
          </PressButton2>
          <PressButton3 onPress={handleBuy}>
            Buy item (10 points)
          </PressButton3>
        </p>
      </header>
    </div>
  );
}
}
export default App;
